//找到字符串中所有字母异位词
public class lt438 {

    //    模拟 超时
    public static List<Integer> findAnagrams(String s, String p) {

        List<Integer> result = new ArrayList<>();

        HashMap<Character, Integer> pfreq = new HashMap<>();

//        统计p的字符频次
        for (int i = 0; i < p.length(); i++) {
            pfreq.put(p.charAt(i), pfreq.getOrDefault(p.charAt(i), 0) + 1);
        }

        for (int i = 0; i < s.length() - p.length() + 1; i++) {
//            统计子串的字符频次
            HashMap<Character, Integer> freq = new HashMap<>();
            for (int j = i; j < i + p.length(); j++) {
                freq.put(s.charAt(j), freq.getOrDefault(s.charAt(j), 0) + 1);
            }

//            判断
            boolean flag = true;
            if (pfreq.size() != freq.size()) {
                flag = false;
            }

            for (Map.Entry entry : pfreq.entrySet()) {
//                包装类的比较要用equals方法
                if (!freq.containsKey(entry.getKey())) {
                    flag = false;
                } else if (freq.get(entry.getKey()).equals(entry.getValue()) == false) {
                    flag = false;
                }
            }

            if (flag == true) {
                result.add(i);
            }

        }

        return result;
    }

    //    滑动窗口
    public static List<Integer> findAnagrams1(String s, String p) {

//        也可以用char数组
        int[] need = new int[26];
        for (int i = 0; i < p.length(); i++) {
            need[p.charAt(i) - 'a']++;
        }

        int start = 0, end = 0;
        int[] window = new int[26];
        List<Integer> result = new ArrayList<>();

        while (end < s.length()) {

            window[s.charAt(end) - 'a']++;

            if (end - start + 1 == p.length()) {
                if (Arrays.equals(window, need)) {
                    result.add(start);
                }
//                窗口滑动
                window[s.charAt(start) - 'a']--;
                start++;
            }

            end++;
        }

        return result;
    }


    public static void main(String[] args) {

        String s = "cbaebabacd";
        String p = "abc";

//        String s = "";
//        String p = "";


        findAnagrams(s, p).forEach(a -> System.out.print(a + " "));
        System.out.println();

        findAnagrams1(s, p).forEach(a -> System.out.print(a + " "));
        System.out.println();


    }

}
